import './styleNavMenu.css';

class MenuItem extends Component{

    constructor(props){
        super(props);

        this.state = {
            name: this.props.name,
            category: this.props.category,
        }


    }

    handleItem = () =>{
        const classItem = 'itemSelected';
        console.log(classItem);
    }
    render(){
        const { name, category } = this.state
        return (
            <div>
                <li className={'item'}>
                    <a href={`#${category}`} onClick={this.handleItem}>{name}</a>
                </li>
            </div> 
        )
    }
}
